import React, { useState } from 'react';
import './App.css';
import axios from 'axios';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import AdminSide from './AdminSide/AdminSide'
import Portal from './ClientSide/Portal'
import SinglePost from './AdminSide/SinglePost'
import Login from './AdminSide/Login';

function App() {
  const [user, setUser] = useState();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");


  const loginHandler = (e) => {
    e.preventDefault()
    axios({
      method: 'post',
      url: 'http://localhost:5000/users/login',
      data: { username, password }
    })
      .then(response => setUser(response.data[0]))
      .catch(error => console.log(error))

  }

  return (
    <div className="App">
      <Router>
        <div>
          <Switch>
            <Route exact path="/admin">
              <Login loginHandler={loginHandler} setUsername={setUsername} setPassword={setPassword} />
            </Route>
            {user &&
              <Route path="/dashboard">
                <AdminSide />
              </Route>}
            <Route exact path="/">
              <Portal />
            </Route>
            <Route exact path="/admin/post/edit/:id" component={SinglePost} />
          </Switch>
        </div>
      </Router>
    </div>
  );
}

export default App;
