import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import SinglePost from './SinglePost';
import { PostsContext } from '../context/postsContext'

function PostsList(props) {
    const [posts, setPosts] = useContext(PostsContext)
    const [modal, setModal] = useState(false)

    useEffect(() => {
        axios({
            method: 'get',
            url: 'http://localhost:5000/posts'
        })
            .then(response => setPosts(response.data))
            .catch(error => console.log(error))
    }, [])

    const deletePost = (e) => {
        e.preventDefault()
        axios({
            method: 'delete',
            url: `http://localhost:5000/posts/${e.target.value}`,
        })
            .then(response => setPosts(response.data))
            .catch(error => console.log(error))
    }


    const fetchDataAfterEdit = () => {
        axios({
            method: 'get',
            url: 'http://localhost:5000/posts'
        })
            .then(response => setPosts(response.data))
            .catch(error => console.log(error))
        setModal(!modal)
    }

    const closeModal = () => setModal(!modal)

    return (
        <div className="">
            {posts && posts.map(post => (
                <div key={post._id}>
                    <h2>{post.title}</h2>
                    <p>{post.post}</p>
                    <button onClick={deletePost} value={post._id}>DELETE</button>
                    <button onClick={() => setModal(true)}>EDIT</button>
                    {modal && <SinglePost closeModal={closeModal} title={post.title} post={post.post} fetchDataAfterEdit={fetchDataAfterEdit} id={post._id} />}

                </div>
            ))}
        </div>
    );
}

export default PostsList;
