import React, { useContext } from 'react';
import './client.css'
import { Link } from "react-router-dom";
import { PostsContext } from '../context/postsContext'



function PostList({ filteredPosts }) {
    const [singlePost, setSinglePost] = useContext(PostsContext)

    const setPost = (e) => {
        console.log(e.target.dataset)
        // setSinglePost()
    }


    return (
        <div className="grid-container">
            {filteredPosts && filteredPosts.map(post => (
                <Link to="/:id" postid="asd" onClick={setPost} data-post={post}>
                    <div className="post">
                        <div className="cover" style={{ background: `url(http://localhost:5000/${post.cover})` }}>
                            <div className="title-wrapper">
                                <h1>{post.title}</h1>
                                <p>{post.category}</p>
                            </div>
                        </div>
                    </div>
                </Link>
            ))}
        </div>
    );
}

export default PostList;
