import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import AdminSide from './AdminSide/AdminSide'
import Portal from './ClientSide/Portal'
import SinglePost from './AdminSide/SinglePost'

function App() {
  const [user, setUser] = useState()

  const getUser = (user) => setUser(user)

  return (
    <div className="App">
      <Router>
        <div>
          <Switch>
            <Route exact path="/admin">
              <AdminSide getUser={getUser} />
            </Route>
            <Route exact path="/">
              <Portal />
            </Route>
            {user &&
              <Route path="/admin/:id" jel="asd" children={<SinglePost />} />
            }
          </Switch>
        </div>
      </Router>
    </div>
  );
}

export default App;
