import React, { useContext, useEffect } from 'react';
import Navigation from './Navigation'
import './client.css'
import axios from 'axios';
import { PostsContext } from '../context/postsContext'
import { Link } from "react-router-dom";


function Post(props) {
    const [singlePost, setSinglePost] = useContext(PostsContext)

    useEffect(() => {
        axios({
            method: 'get',
            url: `http://localhost:5000/posts/${props.match.url.substr(1)}`,
        })
            .then(response => setSinglePost(response.data))
            .catch(error => console.log(error))



    }, [])

    console.log(singlePost)
    return (
        <div >
            <p>asdf</p>
            <Link to="/"><p>Nazad</p></Link>
            <div style={{ background: `url(${props.cover})` }}></div>
            <h1>{props.title}</h1>
        </div>
    );
}

export default Post;
